CREATE TABLE ADVISOR (ID BIGINT NOT NULL, ADDRESS VARCHAR(255), NAME VARCHAR(255) NOT NULL, TAXCODE VARCHAR(255), TELEPHONE VARCHAR(255), COUNTRY1_ID BIGINT, PRIMARY KEY (ID))
CREATE TABLE CLIENT (ID BIGINT NOT NULL, AGE INTEGER NOT NULL, COMMITTEDLUMPSUM FLOAT, COMMITTEDMONTHLYCONTRIBUTIONS FLOAT, DISPOSABLEMONTHLYAMOUNT FLOAT NOT NULL, NAME VARCHAR(255) NOT NULL, NETMONTHLYINCOME FLOAT NOT NULL, TOTALLUMPSUM FLOAT NOT NULL, CLIENTLEVELRISKPROFILE_ID BIGINT, COUNTRY1_ID BIGINT, PRIMARY KEY (ID))
CREATE TABLE COMPANY (ID BIGINT NOT NULL, ADDRESS VARCHAR(255), NAME VARCHAR(255) NOT NULL, TAXCODE VARCHAR(255), TELEPHONE VARCHAR(255), COUNTRY1_ID BIGINT, PRIMARY KEY (ID))
CREATE TABLE COUNTRY (ID BIGINT NOT NULL, LANGUAGE VARCHAR(255), NAME VARCHAR(255) NOT NULL, PRIMARY KEY (ID))
CREATE TABLE GOAL (ID BIGINT NOT NULL, LUMPSUM FLOAT, MONTHLYCONTRIBUTIONS FLOAT, NAME VARCHAR(255) NOT NULL, PERIODYEARS INTEGER NOT NULL, POTENTIALVALUE FLOAT, STARTDATE DATE NOT NULL, TARGETAMOUNT FLOAT NOT NULL, TOTALSHORTFALL FLOAT, CLIENT1_ID BIGINT, GOALLEVELRISKPROFILE_ID BIGINT, PRIMARY KEY (ID))
CREATE TABLE INFLATIONRATE (ID BIGINT NOT NULL, ANNUALINFLATIONRATEPERCENT FLOAT NOT NULL, ENDDATE DATE NOT NULL, INITIALDATE DATE NOT NULL, COUNTRY1_ID BIGINT, PRIMARY KEY (ID))
CREATE TABLE NOMINALGROWTH (ID BIGINT NOT NULL, ABOVEAVERAGE FLOAT NOT NULL, AVERAGE FLOAT NOT NULL, BELOWAVERAGE FLOAT NOT NULL, TERM INTEGER NOT NULL, RISKLEVEL1_ID BIGINT, PRIMARY KEY (ID))
CREATE TABLE RISKLEVEL (ID BIGINT NOT NULL, DESCRIPTION VARCHAR(255) NOT NULL, NAME VARCHAR(255) NOT NULL, PRIMARY KEY (ID))
ALTER TABLE ADVISOR ADD CONSTRAINT FK_ADVISOR_COUNTRY1_ID FOREIGN KEY (COUNTRY1_ID) REFERENCES COUNTRY (ID)
ALTER TABLE CLIENT ADD CONSTRAINT FK_CLIENT_COUNTRY1_ID FOREIGN KEY (COUNTRY1_ID) REFERENCES COUNTRY (ID)
ALTER TABLE CLIENT ADD CONSTRAINT FK_CLIENT_CLIENTLEVELRISKPROFILE_ID FOREIGN KEY (CLIENTLEVELRISKPROFILE_ID) REFERENCES RISKLEVEL (ID)
ALTER TABLE COMPANY ADD CONSTRAINT FK_COMPANY_COUNTRY1_ID FOREIGN KEY (COUNTRY1_ID) REFERENCES COUNTRY (ID)
ALTER TABLE GOAL ADD CONSTRAINT FK_GOAL_GOALLEVELRISKPROFILE_ID FOREIGN KEY (GOALLEVELRISKPROFILE_ID) REFERENCES RISKLEVEL (ID)
ALTER TABLE GOAL ADD CONSTRAINT FK_GOAL_CLIENT1_ID FOREIGN KEY (CLIENT1_ID) REFERENCES CLIENT (ID)
ALTER TABLE INFLATIONRATE ADD CONSTRAINT FK_INFLATIONRATE_COUNTRY1_ID FOREIGN KEY (COUNTRY1_ID) REFERENCES COUNTRY (ID)
ALTER TABLE NOMINALGROWTH ADD CONSTRAINT FK_NOMINALGROWTH_RISKLEVEL1_ID FOREIGN KEY (RISKLEVEL1_ID) REFERENCES RISKLEVEL (ID)
CREATE TABLE SEQUENCE (SEQ_NAME VARCHAR(50) NOT NULL, SEQ_COUNT DECIMAL(38), PRIMARY KEY (SEQ_NAME))
